import React, {Component} from "react";
import './wheelSpin.css';

export default class WheelSpin extends Component {
    render () {
        return(
            <>
                <link href="//maxcdn.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css" rel="stylesheet" />

                <div id="wrapper">
                    <div id="wheel">

