import React, {Component} from 'react';
import WheelComponent from "react-wheel-of-prizes";
import 'react-wheel-of-prizes/dist/index.css';

class WheelSpin extends Component {

    constructor (props) {
        super(props);

        this.segments = ['30', '60', '100', '150', '200', '250']
        this.segmentColors = [ "#EE4040", "#F0CF50", "#815CD1", "#3DA5E0", "#34A24F", "#F9AA1F", "#EC3F3F", "#FF9000", ]

    }


    render () {
        const onFinish = (winner) => {
            console.log(winner)
        }
        return (
            <div>
                <WheelComponent
                    segments={this.segments}
                    segColors={this.segmentColors}
                    winningSegment={30}
                    onFinished={(winner) => onFinish(winner)}
                    primaryColor='black'
                    contrastColor='white'
                    buttonText='Spin'
                />
            </div>
        );
    }
}

export default WheelSpin;
